--Generate settingsmenu texts for the dynamic icons
function FCOIS.GenerateLocalizedDynamicIconTexts()
    --Add texts for settings menu (the different languages)

    --Build texts etc. for non-dynamic icons
    for nonDynIconId=1, numNonDynIcons, 1 do
        local iconToOrdinalStrNonDynEn = iconNrToOrdinalStr[1][tonumber(nonDynIconId)] or "th"
        english["options_icon_sort_" .. tostring(nonDynIconId)]	  = tostring(nonDynIconId).. iconToOrdinalStrNonDynEn
        german["options_icon_sort_" .. tostring(nonDynIconId)]    = tostring(nonDynIconId).."."
        french["options_icon_sort_" .. tostring(nonDynIconId)]    = tostring(nonDynIconId).."."
        --Japanese sort is identical to english so the metatable trick is taking care of it
        russian["options_icon_sort_" .. tostring(nonDynIconId)]   = tostring(nonDynIconId) .."-я пoзиция"
    end
    --Build texts etc. for dynamic icons
    local numDynIconsUsable = FCOIS.settingsVars.settings.numMaxDynamicIconsUsable
    for dynIconId=1, numDynIconsUsable, 1 do
        local fcoisDynIconNr = iconId2FCOISIconNr[dynIconId]
        local isDynamic = isDynamicIcon[fcoisDynIconNr]
        local fcoisLockDynMenuIconNr = lockDynMenuEntryNr2FCOISIconId[dynIconId]

        --Non dynamic icon -> Context menu for the filter buttons "lockDyn" with the first entry: Lock icon
        if fcoisLockDynMenuIconNr == FCOIS_CON_ICON_LOCK then
            english["filter_lockdyn_" .. tostring(fcoisLockDynMenuIconNr)]				= english["options_icon1_color"]
            german["filter_lockdyn_" .. tostring(fcoisLockDynMenuIconNr)]				= german["options_icon1_color"]
            french["filter_lockdyn_" .. tostring(fcoisLockDynMenuIconNr)]				= french["options_icon1_color"]
            spanish["filter_lockdyn_" .. tostring(fcoisLockDynMenuIconNr)]				= spanish["options_icon1_color"]
            italian["filter_lockdyn_" .. tostring(fcoisLockDynMenuIconNr)]				= italian["options_icon1_color"]
            japanese["filter_lockdyn_" .. tostring(fcoisLockDynMenuIconNr)]				= japanese["options_icon1_color"]
            russian["filter_lockdyn_" .. tostring(fcoisLockDynMenuIconNr)]				= russian["options_icon1_color"]
        end
        --Dynamic icon and not the first entry of the lockDyn context menu
        if isDynamic then
            local iconToOrdinalStrEn = iconNrToOrdinalStr[1][tonumber(dynIconId)] or "th"
