function FCOIS.GetLibFiltersPanelIdText(language, libFilterPanelId)
    getLibFiltersPanelIdText = FCOIS.GetLibFiltersPanelIdText
    language = language or FCOIS_CON_LANG_EN
    local retVarTable = {}
    local translatedTexts = {}
    --English
    if     language == FCOIS_CON_LANG_EN then
        translatedTexts[FCOIS_CON_LANG_EN] = {
            [LF_INVENTORY] 					= GetString(SI_INVENTORY_MENU_INVENTORY),
            [LF_BANK_WITHDRAW] 			   	= GetString(SI_CURRENCYLOCATION1) .. " " .. GetString(SI_BANK_WITHDRAW),
