            [LF_GUILDBANK_WITHDRAW] 	    = GetString(SI_CURRENCYLOCATION2) .. " " .. GetString(SI_BANK_WITHDRAW),
            [LF_GUILDBANK_DEPOSIT]	    	= GetString(SI_CURRENCYLOCATION2) .. " " .. GetString(SI_BANK_DEPOSIT),
            [LF_VENDOR_BUY] 				= GetString(SI_GAMEPAD_VENDOR_CATEGORY_HEADER).. " " .. GetString(SI_STORE_MODE_BUY),
            [LF_VENDOR_SELL] 				= GetString(SI_GAMEPAD_VENDOR_CATEGORY_HEADER) .. " " .. GetString(SI_STORE_MODE_SELL),
            [LF_VENDOR_BUYBACK]				= GetString(SI_GAMEPAD_VENDOR_CATEGORY_HEADER).. " " .. GetString(SI_STORE_MODE_BUY),
            [LF_VENDOR_REPAIR] 				= GetString(SI_GAMEPAD_VENDOR_CATEGORY_HEADER).. " " .. GetString(SI_STORE_MODE_BUY_BACK),
            [LF_GUILDSTORE_SELL] 	 		= GetString(SI_WINDOW_TITLE_TRADING_HOUSE) .. " " .. GetString(SI_STORE_MODE_SELL),
            [LF_SMITHING_REFINE]  			= GetString(SI_SMITHING_TAB_REFINEMENT),
            [LF_SMITHING_DECONSTRUCT]  		= GetString(SI_SMITHING_TAB_DECONSTRUCTION),
            [LF_SMITHING_IMPROVEMENT]		= GetString(SI_SMITHING_TAB_IMPROVEMENT),
            [LF_SMITHING_RESEARCH]          = GetString(SI_SMITHING_TAB_RESEARCH),
            [LF_SMITHING_RESEARCH_DIALOG]   = GetString(SI_SMITHING_TAB_RESEARCH) .. " Dialog",
            [LF_MAIL_SEND] 					= GetString(SI_MAIN_MENU_MAIL) .. " " .. GetString(SI_WINDOW_TITLE_SEND_MAIL),
            [LF_TRADE] 						= GetString(SI_WINDOW_TITLE_TRADE),
            [LF_ENCHANTING_CREATION]		= GetString(SI_ITEMFILTERTYPE17) .. " " .. GetString(SI_ENCHANTING_CREATION),
            [LF_ENCHANTING_EXTRACTION]		= GetString(SI_ITEMFILTERTYPE17) .. " " .. GetString(SI_ENCHANTING_EXTRACTION),
            [LF_FENCE_SELL] 				= "Fence " .. GetString(SI_ITEM_ACTION_SELL),
            [LF_FENCE_LAUNDER]				= "Fence " .. GetString(SI_FENCE_LAUNDER_TAB),
            [LF_ALCHEMY_CREATION]			= GetString(SI_ITEMFILTERTYPE16) .. " " .. GetString(SI_ALCHEMY_CREATION),
            [LF_CRAFTBAG]					= GetString(SI_NOTIFICATIONTYPE15),
            [LF_RETRAIT]                    = GetString(SI_RETRAIT_STATION_RETRAIT_MODE),
            [LF_HOUSE_BANK_WITHDRAW]        = GetString(SI_COLLECTIBLECATEGORYTYPE25) .. " " .. GetString(SI_BANK_WITHDRAW),
            [LF_HOUSE_BANK_DEPOSIT]         = GetString(SI_COLLECTIBLECATEGORYTYPE25) .. " " .. GetString(SI_BANK_DEPOSIT),
            [LF_JEWELRY_REFINE]		        = GetString(SI_ITEMFILTERTYPE24) .. " " .. GetString(SI_SMITHING_TAB_REFINEMENT),
            [LF_JEWELRY_DECONSTRUCT]		= GetString(SI_ITEMFILTERTYPE24) .. " " .. GetString(SI_SMITHING_TAB_DECONSTRUCTION),
            [LF_JEWELRY_IMPROVEMENT]		= GetString(SI_ITEMFILTERTYPE24) .. " " .. GetString(SI_SMITHING_TAB_IMPROVEMENT),
            [LF_JEWELRY_RESEARCH]		    = GetString(SI_ITEMFILTERTYPE24) .. " " .. GetString(SI_SMITHING_TAB_RESEARCH),
            [LF_JEWELRY_RESEARCH_DIALOG ]   = GetString(SI_ITEMFILTERTYPE24) .. " " .. GetString(SI_SMITHING_TAB_RESEARCH) .. " Dialog",
            [LF_INVENTORY_COMPANION]		= GetString(SI_UNIT_FRAME_NAME_COMPANION) .. " " .. GetString(SI_INVENTORY_MENU_INVENTORY),
            [FCOIS_CON_LF_CHARACTER]        = GetString(SI_BINDING_NAME_TOGGLE_CHARACTER),
            [FCOIS_CON_LF_COMPANION_CHARACTER] = GetString(SI_UNIT_FRAME_NAME_COMPANION) .. " " .. GetString(SI_BINDING_NAME_TOGGLE_CHARACTER),
        }
    --German
    else
        --Use fallback of English
        if translatedTexts[FCOIS_CON_LANG_EN] == nil then
            translatedTexts[FCOIS_CON_LANG_EN] = getLibFiltersPanelIdText(FCOIS_CON_LANG_EN, nil)
        end
        translatedTexts[language] = translatedTexts[FCOIS_CON_LANG_EN]
        if language == FCOIS_CON_LANG_DE then
            translatedTexts[FCOIS_CON_LANG_DE][LF_FENCE_SELL] 			= "Hehler " .. GetString(SI_ITEM_ACTION_SELL)
            translatedTexts[FCOIS_CON_LANG_DE][LF_FENCE_LAUNDER]		= "Hehler " .. GetString(SI_FENCE_LAUNDER_TAB)
        --French
        elseif language == FCOIS_CON_LANG_FR then
            translatedTexts[FCOIS_CON_LANG_FR][LF_FENCE_SELL] 			= "Receleur " .. GetString(SI_ITEM_ACTION_SELL)
            translatedTexts[FCOIS_CON_LANG_FR][LF_FENCE_LAUNDER]		= "Receleur " .. GetString(SI_FENCE_LAUNDER_TAB)
        --Spanish
        elseif language == FCOIS_CON_LANG_ES then
            translatedTexts[FCOIS_CON_LANG_ES][LF_FENCE_SELL] 			= "Perista " .. GetString(SI_ITEM_ACTION_SELL)
            translatedTexts[FCOIS_CON_LANG_ES][LF_FENCE_LAUNDER]		= "Perista " .. GetString(SI_FENCE_LAUNDER_TAB)
        --Italian
        elseif language == FCOIS_CON_LANG_IT then
            translatedTexts[FCOIS_CON_LANG_IT][LF_FENCE_SELL] 			= "Ricettatore " .. GetString(SI_ITEM_ACTION_SELL)
            translatedTexts[FCOIS_CON_LANG_IT][LF_FENCE_LAUNDER]		= "Ricettatore " .. GetString(SI_FENCE_LAUNDER_TAB)
        --Japanese
        elseif language == FCOIS_CON_LANG_JP then
            translatedTexts[FCOIS_CON_LANG_JP][LF_FENCE_SELL] 			= "盗難商品の購入者 " .. GetString(SI_ITEM_ACTION_SELL)
            translatedTexts[FCOIS_CON_LANG_JP][LF_FENCE_LAUNDER]		= "盗難商品の購入者 " .. GetString(SI_FENCE_LAUNDER_TAB)
        --Russian
        elseif language == FCOIS_CON_LANG_RU then
            translatedTexts[FCOIS_CON_LANG_RU][LF_FENCE_SELL] 			= "скупщик краденого " .. GetString(SI_ITEM_ACTION_SELL)
            translatedTexts[FCOIS_CON_LANG_RU][LF_FENCE_LAUNDER]		= "скупщик краденого " .. GetString(SI_FENCE_LAUNDER_TAB)
        end
    end
    --Return whole table of a language, or only one filter panel data?
    if libFilterPanelId == nil then
        retVarTable = translatedTexts[language]
    else
        --Only return one filter panel data
        retVarTable = translatedTexts[language][libFilterPanelId]
    end
    return retVarTable
end
